import {
  useAddress,
  useClaimNFT,
  useContract,
  useNFTs,
  useTotalCirculatingSupply,
} from '@thirdweb-dev/react'

export default function ClaimNft({ contractAddress }) {
  const address = useAddress()
  const { contract } = useContract(contractAddress)
  const { mutate: claimNFT } = useClaimNFT(contract)
  const { data: totalCirculatingSupply } = useTotalCirculatingSupply(contract)

  const {
    data: nfts,
    isLoading,
    error,
  } = useNFTs(contract, {
    start: Number(totalCirculatingSupply) + 1,
    count: 70,
  })
  if (error) {
    console.error('failed to claim nft', error)
  }
  console.log(nfts)

  if (isLoading) return <p> Loading...</p>
  return (
    <div className="container d-flex justify-content-center m-5">
      <div className="col-xl-3 col-lg-4 col-md-4 col-sm-6 bg-light card border-success">
        <img src={nfts[0].metadata.image} class="card-img-top" />
        <h5 className="card-title mt-3 fst-italic text-body-tertiary">
          {nfts[0].metadata.name}
        </h5>
        <button
          disabled={isLoading}
          onClick={() => claimNFT({ to: address, quantity: 1 })}
          className="btn btn-success m-3 text-info fs-4"
        >
          Claim NFT
        </button>
      </div>
    </div>
  )
}
