import { useContract, useNFT, useTransferNFT } from '@thirdweb-dev/react'
import { useRef, useState } from 'react'

export default function ModalTransfer({ id, contractAddress }) {
  const [isClick, setIsClick] = useState(false)
  const [toAddress, setToAddress] = useState('')
  const refId = useRef('')
  const { contract } = useContract(contractAddress)
  const { data: nft } = useNFT(contract, id)
  const { mutate: transferNFT, isLoading, error } = useTransferNFT(contract)
  if (isLoading)
    return (
      <div class="spinner-border text-primary" role="status">
        <span class="visually-hidden">Loading...</span>
      </div>
    )
  //   function handleTransfer() {
  //     let toAddress = document.querySelector('#walletAddress').value
  //     // transferNFT({
  //     //   to: toAddress,
  //     //   tokenId: id,
  //     // })
  //     if (error) {
  //       alert('failed to transfer NFT')
  //     }
  //     setIsClick(false)
  //   }
  function handleSubmit() {
    // transferNFT({
    //   to: document.querySelector('#walletAddress'),
    //   tokenId: refId.current,
    // })
    console.log(document.querySelector('#walletAddress').value)
  }
  function handleTransfer() {
    refId.current = id
  }
  return (
    <div>
      <button
        className="btn btn-primary m-1"
        data-toggle="modal"
        data-target="#myModal"
        onClick={handleTransfer}
      >
        Transfer
        {id}
        {refId.current}
      </button>
      <div className="modal bg-secondary" id="myModal" role="dialog">
        <div className="d-flex justify-content-center align-items-center w-100">
          <form className="bg-light w-50 mt-5 px-5 py-3 border border-1 rounded">
            {id}
            {refId.current}
            <img src={nft.metadata.image} class="card-img-top w-25" />
            <div className="mb-3">
              <h5 className="card-title text-success">{nft.metadata.name}</h5>
              <input
                type="text"
                placeholder="Enter your wallet address"
                className="form-control"
                id="walletAddress"
              />
            </div>
            <button
              type="button"
              className="btn btn-primary px-5 py-2"
              disabled={isLoading}
              onClick={handleSubmit}
              data-dismiss="modal"
            >
              Send
            </button>
          </form>
        </div>
      </div>
    </div>
  )
}
