import {
  useAddress,
  useContract,
  useContractEvents,
  useContractWrite,
} from '@thirdweb-dev/react'
import { BigNumber } from 'ethers'

export default function MarketPlace() {
  const address = useAddress()
  const contractAddress = '0x905BAe91569339fa21de6aaba9B0564CeBd9F7F4'
  const { contract } = useContract(contractAddress)
  const { mutateAsync: approve, isLoading } = useContractWrite(
    contract,
    'approve'
  )
  const { mutateAsync: transfer } = useContractWrite(contract, 'transfer')
  const { data: event } = useContractEvents(contract, 'Approval')
  console.log(event)
  if (isLoading) return <p>Loading...</p>
  const appr = async () => {
    try {
      const data = await approve({ args: [address, 1] })
      console.log('success')
      console.info('contract call successs', data)
    } catch (err) {
      console.error('contract call failure', err)
      console.log('error')
    }
  }
  const trans = async () => {
    console.log(BigNumber.from(document.getElementById('total').value))
    try {
      const data = await transfer({
        args: [
          '0x9101E40883008206F7adCD4B6c3B69B0d74854f6',
          BigNumber.from(document.getElementById('total').value * 1000000000),
        ],
      })
      console.info('contract call successs', data)
    } catch (err) {
      console.error('contract call failure', err)
    }
  }
  return (
    <div>
      <button onClick={appr}>Approve</button>
      <button onClick={trans}>Send</button>
      <input type="number" id="total" />
    </div>
  )
}
